$(document).ready(function () {
    $('.scrollSection').each(function () {
        const section = $(this)
        const icon = section.find('.scrollSection__icon')
        const content = section.find('.scrollSection__content')

        const triggerOpt = {
            trigger: section,
            start: "top top"
        }

        //icons
        gsap.from(icon, {
            x: 15,
            opacity: 0,
            scrollTrigger: triggerOpt
        })


        if (section.has('.scrollSection__titleBig').length != 0) {
            gsap.from(section.find('.scrollSection__titleBig'), {
                y: "100%",
                opacity: 0,
                scrollTrigger: triggerOpt
            })
        }

        if (section.has('.scrollSection__boldText').length != 0) {
            gsap.from(section.find('.scrollSection__boldText'), {
                y: "100%",
                opacity: 0,
                scrollTrigger: triggerOpt
            })
        }

        if (section.has('.scrollSection__caption').length != 0) {
            gsap.from(section.find('.scrollSection__caption'), {
                opacity: 0,
                scrollTrigger: triggerOpt,
                delay: .5
            })
        }

        if (section.has('.img1').length != 0) {
            gsap.from(section.find('.img1'), {
                opacity: 0,
                x: 35,
                scrollTrigger: triggerOpt,
            })
        }

        if (section.has('.img2').length != 0) {
            gsap.from(section.find('.img2'), {
                opacity: 0,
                x: 25,
                scrollTrigger: triggerOpt,
                delay: .1
            })
        }

        if (section.has('.sc6').length != 0) {
            gsap.from(section.find('.sc6__title'), {
                y: "100%",
                opacity: 0,
                scrollTrigger: triggerOpt,
            })

            gsap.from(section.find('.sc6__buttons'), {
                opacity: 0,
                scrollTrigger: triggerOpt,
                delay: .3
            })

            gsap.from(section.find('.card1'), {
                opacity: 0,
                x: -25,
                scrollTrigger: triggerOpt,
                delay: .2
            })

            gsap.from(section.find('.card2'), {
                opacity: 0,
                x: -25,
                scrollTrigger: triggerOpt,
                delay: .4
            })

            gsap.from(section.find('.card3'), {
                opacity: 0,
                x: 25,
                scrollTrigger: triggerOpt,
                delay: .6
            })

            gsap.from(section.find('.card4'), {
                opacity: 0,
                x: 25,
                scrollTrigger: triggerOpt,
                delay: .8
            })
        }

        if (section.has('.scrollSection__marquee').length != 0) {
            gsap.from(section.find('.scrollSection__marquee'), {
                opacity: 0,
                scrollTrigger: triggerOpt,
            })
        }

        if (section.has('.scrollSection__team').length != 0) {
            gsap.from(section.find('.scrollSection__team'), {
                opacity: 0,
                scrollTrigger: triggerOpt,
            })
        }

        if (section.has('.sc9').length != 0) {
            gsap.from(section.find('.sc9__buttons'), {
                opacity: 0,
                scrollTrigger: triggerOpt,
                delay: .3
            })

            gsap.from(section.find('.sc9__slider'), {
                opacity: 0,
                scrollTrigger: triggerOpt,
                delay: .5
            })

            gsap.from(section.find('.sc9swiper-scrollbar'), {
                opacity: 0,
                scrollTrigger: triggerOpt,
                delay: .5
            })
        }

        if (section.has('.sc10').length != 0) {
            gsap.from(section.find('.sc10__slider'), {
                opacity: 0,
                scrollTrigger: triggerOpt,
                delay: .5
            })

            gsap.from(section.find('.sc10swiper-scrollbar'), {
                opacity: 0,
                scrollTrigger: triggerOpt,
                delay: .5
            })
        }



    })


})

$( document ).ready(function () {
    if ($('.header__search').length > 0) {
        $('.header__search input').keyup(function () {
            if ($('.header__search input').val().length > 0) {
                $('.header__search button').addClass('active')
            } else {
                $('.header__search button').removeClass('active')
            }
        })
    }

    let a = $('.header__second-line li:nth-child(6) a').clone()
    let b = $('.header__second-line li:nth-child(7) a').clone()
    let c = $('.header__second-line li:nth-child(8) a').clone()
    $('.header__second-line-show-more-wrap').append(a)
    $('.header__second-line-show-more-wrap').append(b)
    $('.header__second-line-show-more-wrap').append(c)

    $('.header__second-line-show-more').click(function () {
        $('.header__second-line-show-more-wrap').toggleClass('active');
    })


    $('.header__mobile-button').click(function () {
        if($(this).attr("data-check-mob-menu") == "close-mob-menu"){
            $(this).attr("data-check-mob-menu", 'open-mob-menu');
            $(this).addClass('active')
            $('.header__mobile-hidden').addClass('active');
            $('body').addClass('body_overflow-hidden');
        }else{
            $(this).attr("data-check-mob-menu", 'close-mob-menu');
            $(this).removeClass('active')
            $('.header__mobile-hidden').removeClass('active');
            $('body').removeClass('body_overflow-hidden');
        }
    });

    window.addEventListener('resize', () => {
        // We execute the same script as before
        let vh = window.innerHeight * 0.01;
        document.documentElement.style.setProperty('--vh', `${vh}px`);
    });
})


